mapper.select:
SELECT T.TXT_NUM , T.NM , T.TITLE , T.REG_D, T.HIT FROM ( SELECT B.TXT_NUM , M.NM , B.TITLE , CASE WHEN TO_CHAR(B.REG_D , 'YY.MM.DD') = TO_CHAR(SYSDATE , 'YY.MM.DD') THEN TO_CHAR(B.REG_D, 'HH24:MI') ELSE TO_CHAR(B.REG_D , 'YY.MM.DD') END AS REG_D,HIT, ROW_NUMBER ()OVER(ORDER BY B.TXT_NUM DESC) AS RNUM FROM BB B INNER JOIN MEM M ON B.MEM_NUM = M.MEM_NUM AND M.LEAVE_D IS NULL WHERE B.DEL_D IS NULL
AND M.NM LIKE '%' || #{searchtext} || '%'
and B.TXT_NUM LIKE '%' || #{searchtext} || '%'
and B.TITLE LIKE '%' || #{searchtext} || '%'
) T WHERE T.RNUM BETWEEN #{start} AND #{end}

mapper.update:
		UPDATE BB SET TITLE= #{title},
                    CON =#{con} 
		WHERE TXT_NUM = #{num}
