mapper.select:
SELECT COUNT(*) AS CNT FROM REP WHERE DEL_D IS NULL AND MEM_NUM = #{memnum}
AND REP_NUM LIKE '%' || #{searchtext} || '%'
and CON LIKE '%' || #{searchtext} || '%'

SELECT REP_NUM , TXT_NUM, CON,REG_D FROM( SELECT REP_NUM , TXT_NUM, CON, CASE WHEN TO_CHAR(REG_D , 'YY.MM.DD') = TO_CHAR(SYSDATE , 'YY.MM.DD') THEN TO_CHAR(REG_D, 'HH24:MI') ELSE TO_CHAR(REG_D , 'YY-MM-DD') END AS REG_D, ROW_NUMBER ()OVER(ORDER BY REP_NUM DESC) AS RNUM FROM REP WHERE MEM_NUM= #{memnum} AND DEL_D IS NULL
AND REP_NUM LIKE '%' || #{searchtext} || '%'
and CON LIKE '%' || #{searchtext} || '%'
) A WHERE RNUM BETWEEN #{start} AND #{end}

SELECT COUNT(*) AS CNT FROM REV_REP WHERE DEL_D IS NULL AND MEM_NUM = #{memnum}
AND REP_NUM LIKE '%' || #{searchtext} || '%'
and CON LIKE '%' || #{searchtext} || '%'

SELECT REP_NUM , MEDI_NUM, CON, REG_D FROM( SELECT REP_NUM , MEDI_NUM, REP.CON, CASE WHEN TO_CHAR(REP.REG_D , 'YY.MM.DD') = TO_CHAR(SYSDATE , 'YY.MM.DD') THEN TO_CHAR(REP.REG_D, 'HH24:MI') ELSE TO_CHAR(REP.REG_D , 'YY-MM-DD') END AS REG_D, ROW_NUMBER ()OVER(ORDER BY REP_NUM DESC) AS RNUM FROM REV_REP REP INNER JOIN MEDI_REV REV ON REP.REV_NUM = REV.REV_NUM WHERE REP.MEM_NUM=#{memnum} AND REP.DEL_D IS NULL
AND REP_NUM LIKE '%' || #{searchtext} || '%'
and CON LIKE '%' || #{searchtext} || '%'
) WHERE RNUM BETWEEN #{start} AND #{end}
