mapper.select:
SELECT BM.TXT_NUM , BM.MEM_NUM , BM.TITLE , BM.CON , BM.HIT , BM.DEL_D , BM.REG_D FROM (SELECT B.TXT_NUM , B.MEM_NUM , B.TITLE , B.CON , B.HIT , B.DEL_D ,TO_CHAR(B.REG_D , 'YYYY-MM-DD') AS REG_D , ROW_NUMBER() OVER(ORDER BY B.MEM_NUM DESC) AS RNUM FROM BB B INNER JOIN MEM M ON B.MEM_NUM = M.MEM_NUM WHERE B.MEM_NUM = #{smemnum} AND DEL_D IS NULL
AND B.TXT_NUM LIKE '%' || #{searchTxt} ||'%'
AND B.TITLE LIKE '%' || #{searchTxt} || '%'
) BM WHERE BM.RNUM BETWEEN #{start} AND #{end}

SELECT COUNT(*) AS CNT FROM BB WHERE MEM_NUM = #{smemnum}
AND TXT_NUM LIKE '%' || #{searchTxt} ||'%'
AND TITLE LIKE '%' || #{searchTxt} || '%'
