SELECT MEM_NUM, NM , AUTHOR_CODE, TO_CHAR(BIRTH, 'YYYY-MM-DD') BIRTH , ID , PW , EMAIL, PHONE
FROM MEM
WHERE LEAVE_D IS NULL 
AND ID = #{id}
AND PW = #{pw}

SELECT MEM_NUM, NM , AUTHOR_CODE, TO_CHAR(BIRTH, 'YYYY-MM-DD') BIRTH , ID , PW , EMAIL, PHONE
FROM MEM
WHERE LEAVE_D IS NULL 
AND ID = #{id}
AND PW = #{pw}
AND AUTHOR_CODE = 1

SELECT COUNT(*)
FROM MEM
WHERE EMAIL = #{userEmail}

SELECT NM
FROM MEM
WHERE EMAIL = #{userEmail}

SELECT COUNT(*)
FROM MEM
WHERE ID = #{id}

SELECT NM
FROM MEM
WHERE EMAIL = #{userEmail}
AND ID = #{id}

SELECT COUNT(*)
FROM EMAIL_CERT
WHERE CERT_NUM = #{cert}
AND EMAIL_NUM = #{cur}

SELECT ID,PW
FROM MEM
